#!/bin/bash
# Uninstall script for AirParrot
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling AirParrot..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping AirParrot if running..."
pkill -f "AirParrot" 2>/dev/null || true

# Kill application with bundle ID com.squirrels.AirParrot-3 if running
echo "Stopping application with bundle ID com.squirrels.AirParrot-3 if running..."
killall -9 "com.squirrels.AirParrot-3" 2>/dev/null || true

# Remove /Applications/AirParrot 3.app
echo "Removing /Applications/AirParrot 3.app..."
if [ -d "/Applications/AirParrot 3.app" ]; then
    rm -rf "/Applications/AirParrot 3.app" 2>/dev/null || true
elif [ -f "/Applications/AirParrot 3.app" ]; then
    rm -f "/Applications/AirParrot 3.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.squirrels.AirParrot-*.plist
echo "Removing $HOME/Library/Preferences/com.squirrels.AirParrot-*.plist..."
if [ -d "$HOME/Library/Preferences/com.squirrels.AirParrot-*.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.squirrels.AirParrot-*.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.squirrels.AirParrot-*.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.squirrels.AirParrot-*.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
