#!/bin/bash
# Uninstall script for Alcove
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Alcove..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Alcove if running..."
pkill -f "Alcove" 2>/dev/null || true

# Remove /Applications/Alcove.app
echo "Removing /Applications/Alcove.app..."
if [ -d "/Applications/Alcove.app" ]; then
    rm -rf "/Applications/Alcove.app" 2>/dev/null || true
elif [ -f "/Applications/Alcove.app" ]; then
    rm -f "/Applications/Alcove.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.henrikruscon.Alcove
