#!/bin/bash
# Uninstall script for Bitwig Studio
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Bitwig Studio..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Bitwig Studio if running..."
pkill -f "Bitwig Studio" 2>/dev/null || true

# Remove /Applications/Bitwig Studio.app
echo "Removing /Applications/Bitwig Studio.app..."
if [ -d "/Applications/Bitwig Studio.app" ]; then
    rm -rf "/Applications/Bitwig Studio.app" 2>/dev/null || true
elif [ -f "/Applications/Bitwig Studio.app" ]; then
    rm -f "/Applications/Bitwig Studio.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Bitwig
echo "Removing $HOME/Library/Application Support/Bitwig..."
if [ -d "$HOME/Library/Application Support/Bitwig" ]; then
    rm -rf "$HOME/Library/Application Support/Bitwig" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Bitwig" ]; then
    rm -f "$HOME/Library/Application Support/Bitwig" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/Bitwig
echo "Removing $HOME/Library/Caches/Bitwig..."
if [ -d "$HOME/Library/Caches/Bitwig" ]; then
    rm -rf "$HOME/Library/Caches/Bitwig" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/Bitwig" ]; then
    rm -f "$HOME/Library/Caches/Bitwig" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Bitwig
echo "Removing $HOME/Library/Logs/Bitwig..."
if [ -d "$HOME/Library/Logs/Bitwig" ]; then
    rm -rf "$HOME/Library/Logs/Bitwig" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Bitwig" ]; then
    rm -f "$HOME/Library/Logs/Bitwig" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
