#!/bin/bash
# Uninstall script for Bome Network
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Bome Network..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Bome Network if running..."
pkill -f "Bome Network" 2>/dev/null || true

# Remove /Applications/Bome Network.app
echo "Removing /Applications/Bome Network.app..."
if [ -d "/Applications/Bome Network.app" ]; then
    rm -rf "/Applications/Bome Network.app" 2>/dev/null || true
elif [ -f "/Applications/Bome Network.app" ]; then
    rm -f "/Applications/Bome Network.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.bome.mt.player.plist
echo "Removing $HOME/Library/Preferences/com.bome.mt.player.plist..."
if [ -d "$HOME/Library/Preferences/com.bome.mt.player.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.bome.mt.player.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.bome.mt.player.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.bome.mt.player.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
