#!/bin/bash
# Uninstall script for DeskPad
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling DeskPad..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping DeskPad if running..."
pkill -f "DeskPad" 2>/dev/null || true

# Remove /Applications/DeskPad.app
echo "Removing /Applications/DeskPad.app..."
if [ -d "/Applications/DeskPad.app" ]; then
    rm -rf "/Applications/DeskPad.app" 2>/dev/null || true
elif [ -f "/Applications/DeskPad.app" ]; then
    rm -f "/Applications/DeskPad.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/com.stengo.DeskPad
echo "Removing $HOME/Library/Application Scripts/com.stengo.DeskPad..."
if [ -d "$HOME/Library/Application Scripts/com.stengo.DeskPad" ]; then
    rm -rf "$HOME/Library/Application Scripts/com.stengo.DeskPad" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/com.stengo.DeskPad" ]; then
    rm -f "$HOME/Library/Application Scripts/com.stengo.DeskPad" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.stengo.DeskPad
echo "Removing $HOME/Library/Containers/com.stengo.DeskPad..."
if [ -d "$HOME/Library/Containers/com.stengo.DeskPad" ]; then
    rm -rf "$HOME/Library/Containers/com.stengo.DeskPad" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.stengo.DeskPad" ]; then
    rm -f "$HOME/Library/Containers/com.stengo.DeskPad" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
