#!/bin/bash
# Uninstall script for Dockside
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Dockside..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Dockside if running..."
pkill -f "Dockside" 2>/dev/null || true

# Remove /Applications/Dockside.app
echo "Removing /Applications/Dockside.app..."
if [ -d "/Applications/Dockside.app" ]; then
    rm -rf "/Applications/Dockside.app" 2>/dev/null || true
elif [ -f "/Applications/Dockside.app" ]; then
    rm -f "/Applications/Dockside.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.hachipoo.Dockside.plist
echo "Removing $HOME/Library/Preferences/com.hachipoo.Dockside.plist..."
if [ -d "$HOME/Library/Preferences/com.hachipoo.Dockside.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.hachipoo.Dockside.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.hachipoo.Dockside.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.hachipoo.Dockside.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
