#!/bin/bash
# Uninstall script for Fathom
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Fathom..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Fathom if running..."
pkill -f "Fathom" 2>/dev/null || true

# Kill application with bundle ID Fathom Helper if running
echo "Stopping application with bundle ID Fathom Helper if running..."
killall -9 "Fathom Helper" 2>/dev/null || true

# Kill application with bundle ID Fathom if running
echo "Stopping application with bundle ID Fathom if running..."
killall -9 "Fathom" 2>/dev/null || true

# Remove /Applications/Fathom.app
echo "Removing /Applications/Fathom.app..."
if [ -d "/Applications/Fathom.app" ]; then
    rm -rf "/Applications/Fathom.app" 2>/dev/null || true
elif [ -f "/Applications/Fathom.app" ]; then
    rm -f "/Applications/Fathom.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Fathom
echo "Removing $HOME/Library/Application Support/Fathom..."
if [ -d "$HOME/Library/Application Support/Fathom" ]; then
    rm -rf "$HOME/Library/Application Support/Fathom" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Fathom" ]; then
    rm -f "$HOME/Library/Application Support/Fathom" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Fathom
echo "Removing $HOME/Library/Logs/Fathom..."
if [ -d "$HOME/Library/Logs/Fathom" ]; then
    rm -rf "$HOME/Library/Logs/Fathom" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Fathom" ]; then
    rm -f "$HOME/Library/Logs/Fathom" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
