#!/bin/bash
# Uninstall script for FlowVision
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling FlowVision..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping FlowVision if running..."
pkill -f "FlowVision" 2>/dev/null || true

# Remove /Applications/FlowVision.app
echo "Removing /Applications/FlowVision.app..."
if [ -d "/Applications/FlowVision.app" ]; then
    rm -rf "/Applications/FlowVision.app" 2>/dev/null || true
elif [ -f "/Applications/FlowVision.app" ]; then
    rm -f "/Applications/FlowVision.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/FlowVision
echo "Removing $HOME/Library/Application Support/FlowVision..."
if [ -d "$HOME/Library/Application Support/FlowVision" ]; then
    rm -rf "$HOME/Library/Application Support/FlowVision" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/FlowVision" ]; then
    rm -f "$HOME/Library/Application Support/FlowVision" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/netdcy.FlowVision.plist
echo "Removing $HOME/Library/Preferences/netdcy.FlowVision.plist..."
if [ -d "$HOME/Library/Preferences/netdcy.FlowVision.plist" ]; then
    rm -rf "$HOME/Library/Preferences/netdcy.FlowVision.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/netdcy.FlowVision.plist" ]; then
    rm -f "$HOME/Library/Preferences/netdcy.FlowVision.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/netdcy.FlowVision.savedState
echo "Removing $HOME/Library/Saved Application State/netdcy.FlowVision.savedState..."
if [ -d "$HOME/Library/Saved Application State/netdcy.FlowVision.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/netdcy.FlowVision.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/netdcy.FlowVision.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/netdcy.FlowVision.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
