#!/bin/bash
# Uninstall script for Gephi
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Gephi..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Gephi if running..."
pkill -f "Gephi" 2>/dev/null || true

# Remove /Applications/Gephi.app
echo "Removing /Applications/Gephi.app..."
if [ -d "/Applications/Gephi.app" ]; then
    rm -rf "/Applications/Gephi.app" 2>/dev/null || true
elif [ -f "/Applications/Gephi.app" ]; then
    rm -f "/Applications/Gephi.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/gephi
echo "Removing $HOME/Library/Application Support/gephi..."
if [ -d "$HOME/Library/Application Support/gephi" ]; then
    rm -rf "$HOME/Library/Application Support/gephi" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/gephi" ]; then
    rm -f "$HOME/Library/Application Support/gephi" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/gephi
echo "Removing $HOME/Library/Caches/gephi..."
if [ -d "$HOME/Library/Caches/gephi" ]; then
    rm -rf "$HOME/Library/Caches/gephi" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/gephi" ]; then
    rm -f "$HOME/Library/Caches/gephi" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
