#!/bin/bash
# Uninstall script for HEY
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling HEY..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping HEY if running..."
pkill -f "HEY" 2>/dev/null || true

# Remove /Applications/HEY.app
echo "Removing /Applications/HEY.app..."
if [ -d "/Applications/HEY.app" ]; then
    rm -rf "/Applications/HEY.app" 2>/dev/null || true
elif [ -f "/Applications/HEY.app" ]; then
    rm -f "/Applications/HEY.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/HEY
echo "Removing $HOME/Library/Application Support/HEY..."
if [ -d "$HOME/Library/Application Support/HEY" ]; then
    rm -rf "$HOME/Library/Application Support/HEY" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/HEY" ]; then
    rm -f "$HOME/Library/Application Support/HEY" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.hey.app.desktop.plist
echo "Removing $HOME/Library/Preferences/com.hey.app.desktop.plist..."
if [ -d "$HOME/Library/Preferences/com.hey.app.desktop.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.hey.app.desktop.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.hey.app.desktop.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.hey.app.desktop.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
