#!/bin/bash
# Uninstall script for Highlight
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Highlight..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Highlight if running..."
pkill -f "Highlight" 2>/dev/null || true

# Remove /Applications/Highlight.app
echo "Removing /Applications/Highlight.app..."
if [ -d "/Applications/Highlight.app" ]; then
    rm -rf "/Applications/Highlight.app" 2>/dev/null || true
elif [ -f "/Applications/Highlight.app" ]; then
    rm -f "/Applications/Highlight.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Highlight
echo "Removing $HOME/Library/Application Support/Highlight..."
if [ -d "$HOME/Library/Application Support/Highlight" ]; then
    rm -rf "$HOME/Library/Application Support/Highlight" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Highlight" ]; then
    rm -f "$HOME/Library/Application Support/Highlight" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/tv.medal.highlight.plist
echo "Removing $HOME/Library/Preferences/tv.medal.highlight.plist..."
if [ -d "$HOME/Library/Preferences/tv.medal.highlight.plist" ]; then
    rm -rf "$HOME/Library/Preferences/tv.medal.highlight.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/tv.medal.highlight.plist" ]; then
    rm -f "$HOME/Library/Preferences/tv.medal.highlight.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/tv.medal.highlight.savedState
echo "Removing $HOME/Library/Saved Application State/tv.medal.highlight.savedState..."
if [ -d "$HOME/Library/Saved Application State/tv.medal.highlight.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/tv.medal.highlight.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/tv.medal.highlight.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/tv.medal.highlight.savedState" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
