#!/bin/bash
# Uninstall script for macFUSE
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling macFUSE..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping macFUSE if running..."
