# Remove /Library/PreferencePanes/macFUSE.prefPane
echo "Removing /Library/PreferencePanes/macFUSE.prefPane..."
if [ -d "/Library/PreferencePanes/macFUSE.prefPane" ]; then
    rm -rf "/Library/PreferencePanes/macFUSE.prefPane" 2>/dev/null || true
elif [ -f "/Library/PreferencePanes/macFUSE.prefPane" ]; then
    rm -f "/Library/PreferencePanes/macFUSE.prefPane" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/io.macfuse.preferencepanes.macfuse
echo "Removing $HOME/Library/Caches/io.macfuse.preferencepanes.macfuse..."
