elif [ -f "$HOME/Library/Caches/io.macfuse.preferencepanes.macfuse" ]; then
    rm -f "$HOME/Library/Caches/io.macfuse.preferencepanes.macfuse" 2>/dev/null || true
fi

# Remove $HOME/Library/HTTPStorages/io.macfuse.preferencepanes.macfuse
