#!/bin/bash
# Uninstall script for MagicQuit
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling MagicQuit..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping MagicQuit if running..."
pkill -f "MagicQuit" 2>/dev/null || true

# Remove /Applications/MagicQuit.app
echo "Removing /Applications/MagicQuit.app..."
if [ -d "/Applications/MagicQuit.app" ]; then
    rm -rf "/Applications/MagicQuit.app" 2>/dev/null || true
elif [ -f "/Applications/MagicQuit.app" ]; then
    rm -f "/Applications/MagicQuit.app" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
