#!/bin/bash
# Uninstall script for Mixxx
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Mixxx..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Mixxx if running..."
pkill -f "Mixxx" 2>/dev/null || true

# Remove /Applications/Mixxx.app
echo "Removing /Applications/Mixxx.app..."
if [ -d "/Applications/Mixxx.app" ]; then
    rm -rf "/Applications/Mixxx.app" 2>/dev/null || true
elif [ -f "/Applications/Mixxx.app" ]; then
    rm -f "/Applications/Mixxx.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/org.mixxx.mixxx
echo "Removing $HOME/Library/Application Scripts/org.mixxx.mixxx..."
if [ -d "$HOME/Library/Application Scripts/org.mixxx.mixxx" ]; then
    rm -rf "$HOME/Library/Application Scripts/org.mixxx.mixxx" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/org.mixxx.mixxx" ]; then
    rm -f "$HOME/Library/Application Scripts/org.mixxx.mixxx" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/org.mixxx.mixxx
echo "Removing $HOME/Library/Containers/org.mixxx.mixxx..."
if [ -d "$HOME/Library/Containers/org.mixxx.mixxx" ]; then
    rm -rf "$HOME/Library/Containers/org.mixxx.mixxx" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/org.mixxx.mixxx" ]; then
    rm -f "$HOME/Library/Containers/org.mixxx.mixxx" 2>/dev/null || true
fi

# Remove $HOME/Music/Mixxx
echo "Removing $HOME/Music/Mixxx..."
if [ -d "$HOME/Music/Mixxx" ]; then
    rm -rf "$HOME/Music/Mixxx" 2>/dev/null || true
elif [ -f "$HOME/Music/Mixxx" ]; then
    rm -f "$HOME/Music/Mixxx" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
