#!/bin/bash
# Uninstall script for Mobirise
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Mobirise..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Mobirise if running..."
pkill -f "Mobirise" 2>/dev/null || true

# Remove /Applications/Mobirise.app
echo "Removing /Applications/Mobirise.app..."
if [ -d "/Applications/Mobirise.app" ]; then
    rm -rf "/Applications/Mobirise.app" 2>/dev/null || true
elif [ -f "/Applications/Mobirise.app" ]; then
    rm -f "/Applications/Mobirise.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Mobirise
echo "Removing $HOME/Library/Application Support/Mobirise..."
if [ -d "$HOME/Library/Application Support/Mobirise" ]; then
    rm -rf "$HOME/Library/Application Support/Mobirise" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Mobirise" ]; then
    rm -f "$HOME/Library/Application Support/Mobirise" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Mobirise.com
echo "Removing $HOME/Library/Application Support/Mobirise.com..."
if [ -d "$HOME/Library/Application Support/Mobirise.com" ]; then
    rm -rf "$HOME/Library/Application Support/Mobirise.com" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Mobirise.com" ]; then
    rm -f "$HOME/Library/Application Support/Mobirise.com" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.mobirise.Mobirise.plist
echo "Removing $HOME/Library/Preferences/com.mobirise.Mobirise.plist..."
if [ -d "$HOME/Library/Preferences/com.mobirise.Mobirise.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.mobirise.Mobirise.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.mobirise.Mobirise.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.mobirise.Mobirise.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.mobirise.Mobirise.savedState
echo "Removing $HOME/Library/Saved Application State/com.mobirise.Mobirise.savedState..."
if [ -d "$HOME/Library/Saved Application State/com.mobirise.Mobirise.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.mobirise.Mobirise.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.mobirise.Mobirise.savedState" ]; then
