#!/bin/bash
# Uninstall script for Netiquette
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Netiquette..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Netiquette if running..."
pkill -f "Netiquette" 2>/dev/null || true

# Remove /Applications/Netiquette.app
echo "Removing /Applications/Netiquette.app..."
if [ -d "/Applications/Netiquette.app" ]; then
    rm -rf "/Applications/Netiquette.app" 2>/dev/null || true
elif [ -f "/Applications/Netiquette.app" ]; then
    rm -f "/Applications/Netiquette.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.objective-see.Netiquette
echo "Removing $HOME/Library/Caches/com.objective-see.Netiquette..."
if [ -d "$HOME/Library/Caches/com.objective-see.Netiquette" ]; then
    rm -rf "$HOME/Library/Caches/com.objective-see.Netiquette" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.objective-see.Netiquette" ]; then
    rm -f "$HOME/Library/Caches/com.objective-see.Netiquette" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.objective-see.Netiquette.plist
echo "Removing $HOME/Library/Preferences/com.objective-see.Netiquette.plist..."
if [ -d "$HOME/Library/Preferences/com.objective-see.Netiquette.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.objective-see.Netiquette.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.objective-see.Netiquette.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.objective-see.Netiquette.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
