#!/bin/bash
# Uninstall script for NVIDIA GeForce NOW
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling NVIDIA GeForce NOW..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping NVIDIA GeForce NOW if running..."
pkill -f "NVIDIA GeForce NOW" 2>/dev/null || true

# Remove /Applications/GeForceNOW.app
echo "Removing /Applications/GeForceNOW.app..."
if [ -d "/Applications/GeForceNOW.app" ]; then
    rm -rf "/Applications/GeForceNOW.app" 2>/dev/null || true
elif [ -f "/Applications/GeForceNOW.app" ]; then
    rm -f "/Applications/GeForceNOW.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/NVIDIA/GeForceNOW
echo "Removing $HOME/Library/Application Support/NVIDIA/GeForceNOW..."
if [ -d "$HOME/Library/Application Support/NVIDIA/GeForceNOW" ]; then
