#!/bin/bash
# Uninstall script for Orka Desktop
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Orka Desktop..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Orka Desktop if running..."
pkill -f "Orka Desktop" 2>/dev/null || true

# Remove /Applications/Orka Desktop.app
echo "Removing /Applications/Orka Desktop.app..."
if [ -d "/Applications/Orka Desktop.app" ]; then
    rm -rf "/Applications/Orka Desktop.app" 2>/dev/null || true
elif [ -f "/Applications/Orka Desktop.app" ]; then
    rm -f "/Applications/Orka Desktop.app" 2>/dev/null || true
fi

# Remove $HOME/orka-toolkit/
echo "Removing $HOME/orka-toolkit/..."
if [ -d "$HOME/orka-toolkit/" ]; then
    rm -rf "$HOME/orka-toolkit/" 2>/dev/null || true
elif [ -f "$HOME/orka-toolkit/" ]; then
    rm -f "$HOME/orka-toolkit/" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
