#!/bin/bash
# Uninstall script for OverSight
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling OverSight..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping OverSight if running..."
pkill -f "OverSight" 2>/dev/null || true

# Remove $HOME/Library/Caches/com.objective-see.oversight
echo "Removing $HOME/Library/Caches/com.objective-see.oversight..."
if [ -d "$HOME/Library/Caches/com.objective-see.oversight" ]; then
    rm -rf "$HOME/Library/Caches/com.objective-see.oversight" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.objective-see.oversight" ]; then
    rm -f "$HOME/Library/Caches/com.objective-see.oversight" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.objective-see.OverSightHelper
echo "Removing $HOME/Library/Caches/com.objective-see.OverSightHelper..."
if [ -d "$HOME/Library/Caches/com.objective-see.OverSightHelper" ]; then
    rm -rf "$HOME/Library/Caches/com.objective-see.OverSightHelper" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.objective-see.OverSightHelper" ]; then
    rm -f "$HOME/Library/Caches/com.objective-see.OverSightHelper" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.objective-see.oversight.plist
echo "Removing $HOME/Library/Preferences/com.objective-see.oversight.plist..."
if [ -d "$HOME/Library/Preferences/com.objective-see.oversight.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.objective-see.oversight.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.objective-see.oversight.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.objective-see.oversight.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.objective-see.OverSightHelper.plist
echo "Removing $HOME/Library/Preferences/com.objective-see.OverSightHelper.plist..."
if [ -d "$HOME/Library/Preferences/com.objective-see.OverSightHelper.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.objective-see.OverSightHelper.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.objective-see.OverSightHelper.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.objective-see.OverSightHelper.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
