#!/bin/bash
# Uninstall script for Packages
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Packages..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Packages if running..."
pkill -f "Packages" 2>/dev/null || true

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/fr.whitebox.packages.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/fr.whitebox.packages.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/fr.whitebox.packages.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/fr.whitebox.packages.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/fr.whitebox.packages.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/fr.whitebox.packages.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/fr.whitebox.Packages
echo "Removing $HOME/Library/Caches/fr.whitebox.Packages..."
if [ -d "$HOME/Library/Caches/fr.whitebox.Packages" ]; then
    rm -rf "$HOME/Library/Caches/fr.whitebox.Packages" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/fr.whitebox.Packages" ]; then
    rm -f "$HOME/Library/Caches/fr.whitebox.Packages" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/fr.whitebox.Packages.plist
echo "Removing $HOME/Library/Preferences/fr.whitebox.Packages.plist..."
if [ -d "$HOME/Library/Preferences/fr.whitebox.Packages.plist" ]; then
    rm -rf "$HOME/Library/Preferences/fr.whitebox.Packages.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/fr.whitebox.Packages.plist" ]; then
    rm -f "$HOME/Library/Preferences/fr.whitebox.Packages.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
