#!/bin/bash
# Uninstall script for Parallels Client
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Parallels Client..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Parallels Client if running..."
pkill -f "Parallels Client" 2>/dev/null || true

# Kill application with bundle ID com.2X.Client.Mac if running
echo "Stopping application with bundle ID com.2X.Client.Mac if running..."
killall -9 "com.2X.Client.Mac" 2>/dev/null || true

# Remove $HOME/Library/Application Support/com.2X.Client.Mac
echo "Removing $HOME/Library/Application Support/com.2X.Client.Mac..."
if [ -d "$HOME/Library/Application Support/com.2X.Client.Mac" ]; then
    rm -rf "$HOME/Library/Application Support/com.2X.Client.Mac" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.2X.Client.Mac" ]; then
    rm -f "$HOME/Library/Application Support/com.2X.Client.Mac" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.2X.Client.Mac
echo "Removing $HOME/Library/Containers/com.2X.Client.Mac..."
if [ -d "$HOME/Library/Containers/com.2X.Client.Mac" ]; then
    rm -rf "$HOME/Library/Containers/com.2X.Client.Mac" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.2X.Client.Mac" ]; then
    rm -f "$HOME/Library/Containers/com.2X.Client.Mac" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
