#!/bin/bash
# Uninstall script for Phoenix Slides
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Phoenix Slides..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Phoenix Slides if running..."
pkill -f "Phoenix Slides" 2>/dev/null || true

# Remove /Applications/Phoenix Slides.app
echo "Removing /Applications/Phoenix Slides.app..."
if [ -d "/Applications/Phoenix Slides.app" ]; then
    rm -rf "/Applications/Phoenix Slides.app" 2>/dev/null || true
elif [ -f "/Applications/Phoenix Slides.app" ]; then
    rm -f "/Applications/Phoenix Slides.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.apple.helpd/Generated/Phoenix Slides Help*
echo "Removing $HOME/Library/Caches/com.apple.helpd/Generated/Phoenix Slides Help*..."
if [ -d "$HOME/Library/Caches/com.apple.helpd/Generated/Phoenix Slides Help*" ]; then
    rm -rf "$HOME/Library/Caches/com.apple.helpd/Generated/Phoenix Slides Help*" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.apple.helpd/Generated/Phoenix Slides Help*" ]; then
    rm -f "$HOME/Library/Caches/com.apple.helpd/Generated/Phoenix Slides Help*" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/net.blyt.phoenixslides.plist
echo "Removing $HOME/Library/Preferences/net.blyt.phoenixslides.plist..."
if [ -d "$HOME/Library/Preferences/net.blyt.phoenixslides.plist" ]; then
    rm -rf "$HOME/Library/Preferences/net.blyt.phoenixslides.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/net.blyt.phoenixslides.plist" ]; then
    rm -f "$HOME/Library/Preferences/net.blyt.phoenixslides.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
