#!/bin/bash
# Uninstall script for PiPHero
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling PiPHero..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping PiPHero if running..."
pkill -f "PiPHero" 2>/dev/null || true

# Remove /Applications/PiPHero.app
echo "Removing /Applications/PiPHero.app..."
if [ -d "/Applications/PiPHero.app" ]; then
    rm -rf "/Applications/PiPHero.app" 2>/dev/null || true
elif [ -f "/Applications/PiPHero.app" ]; then
    rm -f "/Applications/PiPHero.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/piphero
echo "Removing $HOME/Library/Application Support/piphero..."
if [ -d "$HOME/Library/Application Support/piphero" ]; then
    rm -rf "$HOME/Library/Application Support/piphero" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/piphero" ]; then
    rm -f "$HOME/Library/Application Support/piphero" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.piphero.app.plist
echo "Removing $HOME/Library/Preferences/com.piphero.app.plist..."
if [ -d "$HOME/Library/Preferences/com.piphero.app.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.piphero.app.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.piphero.app.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.piphero.app.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
