#!/bin/bash
# Uninstall script for Platypus
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Platypus..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Platypus if running..."
pkill -f "Platypus" 2>/dev/null || true

# Remove /Applications/Platypus.app
echo "Removing /Applications/Platypus.app..."
if [ -d "/Applications/Platypus.app" ]; then
    rm -rf "/Applications/Platypus.app" 2>/dev/null || true
elif [ -f "/Applications/Platypus.app" ]; then
    rm -f "/Applications/Platypus.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.sveinbjorn.platypus.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.sveinbjorn.platypus.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.sveinbjorn.platypus.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.sveinbjorn.platypus.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.sveinbjorn.platypus.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/org.sveinbjorn.platypus.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Platypus
echo "Removing $HOME/Library/Application Support/Platypus..."
if [ -d "$HOME/Library/Application Support/Platypus" ]; then
    rm -rf "$HOME/Library/Application Support/Platypus" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Platypus" ]; then
    rm -f "$HOME/Library/Application Support/Platypus" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/org.sveinbjorn.Platypus
echo "Removing $HOME/Library/Caches/org.sveinbjorn.Platypus..."
if [ -d "$HOME/Library/Caches/org.sveinbjorn.Platypus" ]; then
    rm -rf "$HOME/Library/Caches/org.sveinbjorn.Platypus" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/org.sveinbjorn.Platypus" ]; then
    rm -f "$HOME/Library/Caches/org.sveinbjorn.Platypus" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/org.sveinbjorn.Platypus.plist
echo "Removing $HOME/Library/Preferences/org.sveinbjorn.Platypus.plist..."
if [ -d "$HOME/Library/Preferences/org.sveinbjorn.Platypus.plist" ]; then
    rm -rf "$HOME/Library/Preferences/org.sveinbjorn.Platypus.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/org.sveinbjorn.Platypus.plist" ]; then
    rm -f "$HOME/Library/Preferences/org.sveinbjorn.Platypus.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
