#!/bin/bash
# Uninstall script for Reflector
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Reflector..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Reflector if running..."
pkill -f "Reflector" 2>/dev/null || true

# Remove /Applications/Reflector 4.app
echo "Removing /Applications/Reflector 4.app..."
if [ -d "/Applications/Reflector 4.app" ]; then
    rm -rf "/Applications/Reflector 4.app" 2>/dev/null || true
elif [ -f "/Applications/Reflector 4.app" ]; then
    rm -f "/Applications/Reflector 4.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/Logs/Reflector*.log*
echo "Removing $HOME/Library/Application Support/Logs/Reflector*.log*..."
if [ -d "$HOME/Library/Application Support/Logs/Reflector*.log*" ]; then
    rm -rf "$HOME/Library/Application Support/Logs/Reflector*.log*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Logs/Reflector*.log*" ]; then
    rm -f "$HOME/Library/Application Support/Logs/Reflector*.log*" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/com.squirrels.Reflector-*
echo "Removing $HOME/Library/Caches/com.squirrels.Reflector-*..."
if [ -d "$HOME/Library/Caches/com.squirrels.Reflector-*" ]; then
    rm -rf "$HOME/Library/Caches/com.squirrels.Reflector-*" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.squirrels.Reflector-*" ]; then
    rm -f "$HOME/Library/Caches/com.squirrels.Reflector-*" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.squirrels.Reflector-*.plist
echo "Removing $HOME/Library/Preferences/com.squirrels.Reflector-*.plist..."
if [ -d "$HOME/Library/Preferences/com.squirrels.Reflector-*.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.squirrels.Reflector-*.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.squirrels.Reflector-*.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.squirrels.Reflector-*.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
