#!/bin/bash
# Uninstall script for Rive
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Rive..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Rive if running..."
pkill -f "Rive" 2>/dev/null || true

# Remove /Applications/Rive.app
echo "Removing /Applications/Rive.app..."
if [ -d "/Applications/Rive.app" ]; then
    rm -rf "/Applications/Rive.app" 2>/dev/null || true
elif [ -f "/Applications/Rive.app" ]; then
    rm -f "/Applications/Rive.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Scripts/app.rive.editor
echo "Removing $HOME/Library/Application Scripts/app.rive.editor..."
if [ -d "$HOME/Library/Application Scripts/app.rive.editor" ]; then
    rm -rf "$HOME/Library/Application Scripts/app.rive.editor" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Scripts/app.rive.editor" ]; then
    rm -f "$HOME/Library/Application Scripts/app.rive.editor" 2>/dev/null || true
fi

# Remove $HOME/Library/Caches/app.rive.editor
echo "Removing $HOME/Library/Caches/app.rive.editor..."
if [ -d "$HOME/Library/Caches/app.rive.editor" ]; then
    rm -rf "$HOME/Library/Caches/app.rive.editor" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/app.rive.editor" ]; then
    rm -f "$HOME/Library/Caches/app.rive.editor" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/app.rive.editor
echo "Removing $HOME/Library/Containers/app.rive.editor..."
if [ -d "$HOME/Library/Containers/app.rive.editor" ]; then
    rm -rf "$HOME/Library/Containers/app.rive.editor" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/app.rive.editor" ]; then
    rm -f "$HOME/Library/Containers/app.rive.editor" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
