#!/bin/bash
# Uninstall script for Rode Central
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Rode Central..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Rode Central if running..."
pkill -f "Rode Central" 2>/dev/null || true

# Remove $HOME/Library/Caches/com.rode.rodecentral
echo "Removing $HOME/Library/Caches/com.rode.rodecentral..."
if [ -d "$HOME/Library/Caches/com.rode.rodecentral" ]; then
    rm -rf "$HOME/Library/Caches/com.rode.rodecentral" 2>/dev/null || true
elif [ -f "$HOME/Library/Caches/com.rode.rodecentral" ]; then
    rm -f "$HOME/Library/Caches/com.rode.rodecentral" 2>/dev/null || true
fi

# Remove $HOME/Library/HTTPStorages/com.rode.rodecentral
echo "Removing $HOME/Library/HTTPStorages/com.rode.rodecentral..."
if [ -d "$HOME/Library/HTTPStorages/com.rode.rodecentral" ]; then
    rm -rf "$HOME/Library/HTTPStorages/com.rode.rodecentral" 2>/dev/null || true
elif [ -f "$HOME/Library/HTTPStorages/com.rode.rodecentral" ]; then
    rm -f "$HOME/Library/HTTPStorages/com.rode.rodecentral" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
