#!/bin/bash
# Uninstall script for Rode Connect
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Rode Connect..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Rode Connect if running..."
pkill -f "Rode Connect" 2>/dev/null || true

# Remove $HOME/Library/Cache/com.rode.rodeconnect
echo "Removing $HOME/Library/Cache/com.rode.rodeconnect..."
if [ -d "$HOME/Library/Cache/com.rode.rodeconnect" ]; then
    rm -rf "$HOME/Library/Cache/com.rode.rodeconnect" 2>/dev/null || true
elif [ -f "$HOME/Library/Cache/com.rode.rodeconnect" ]; then
    rm -f "$HOME/Library/Cache/com.rode.rodeconnect" 2>/dev/null || true
fi

# Remove $HOME/Library/HTTPStorages/com.rode.rodeconnect
echo "Removing $HOME/Library/HTTPStorages/com.rode.rodeconnect..."
if [ -d "$HOME/Library/HTTPStorages/com.rode.rodeconnect" ]; then
    rm -rf "$HOME/Library/HTTPStorages/com.rode.rodeconnect" 2>/dev/null || true
elif [ -f "$HOME/Library/HTTPStorages/com.rode.rodeconnect" ]; then
    rm -f "$HOME/Library/HTTPStorages/com.rode.rodeconnect" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.rode.rodeconnect.plist
echo "Removing $HOME/Library/Preferences/com.rode.rodeconnect.plist..."
if [ -d "$HOME/Library/Preferences/com.rode.rodeconnect.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.rode.rodeconnect.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.rode.rodeconnect.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.rode.rodeconnect.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
