#!/bin/bash
# Uninstall script for SABnzbd
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling SABnzbd..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping SABnzbd if running..."
pkill -f "SABnzbd" 2>/dev/null || true

# Remove /Applications/SABnzbd.app
echo "Removing /Applications/SABnzbd.app..."
if [ -d "/Applications/SABnzbd.app" ]; then
    rm -rf "/Applications/SABnzbd.app" 2>/dev/null || true
elif [ -f "/Applications/SABnzbd.app" ]; then
    rm -f "/Applications/SABnzbd.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/SABnzbd
echo "Removing $HOME/Library/Application Support/SABnzbd..."
if [ -d "$HOME/Library/Application Support/SABnzbd" ]; then
    rm -rf "$HOME/Library/Application Support/SABnzbd" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/SABnzbd" ]; then
    rm -f "$HOME/Library/Application Support/SABnzbd" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
