#!/bin/bash
# Uninstall script for SQLPro Studio
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling SQLPro Studio..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping SQLPro Studio if running..."
pkill -f "SQLPro Studio" 2>/dev/null || true

# Remove /Applications/SQLPro Studio.app
echo "Removing /Applications/SQLPro Studio.app..."
if [ -d "/Applications/SQLPro Studio.app" ]; then
    rm -rf "/Applications/SQLPro Studio.app" 2>/dev/null || true
elif [ -f "/Applications/SQLPro Studio.app" ]; then
    rm -f "/Applications/SQLPro Studio.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.hankinsoft.osx.sqlprostudio.sfl*
echo "Removing $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.hankinsoft.osx.sqlprostudio.sfl*..."
if [ -d "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.hankinsoft.osx.sqlprostudio.sfl*" ]; then
    rm -rf "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.hankinsoft.osx.sqlprostudio.sfl*" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.hankinsoft.osx.sqlprostudio.sfl*" ]; then
    rm -f "$HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.hankinsoft.osx.sqlprostudio.sfl*" 2>/dev/null || true
fi

# Remove $HOME/Library/Containers/com.hankinsoft.osx.sqlprostudio
echo "Removing $HOME/Library/Containers/com.hankinsoft.osx.sqlprostudio..."
if [ -d "$HOME/Library/Containers/com.hankinsoft.osx.sqlprostudio" ]; then
    rm -rf "$HOME/Library/Containers/com.hankinsoft.osx.sqlprostudio" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.hankinsoft.osx.sqlprostudio" ]; then
    rm -f "$HOME/Library/Containers/com.hankinsoft.osx.sqlprostudio" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
