#!/bin/bash
# Uninstall script for Studio 3T
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Studio 3T..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Studio 3T if running..."
pkill -f "Studio 3T" 2>/dev/null || true

# Remove /Applications/Studio 3T.app
echo "Removing /Applications/Studio 3T.app..."
if [ -d "/Applications/Studio 3T.app" ]; then
    rm -rf "/Applications/Studio 3T.app" 2>/dev/null || true
elif [ -f "/Applications/Studio 3T.app" ]; then
    rm -f "/Applications/Studio 3T.app" 2>/dev/null || true
fi

# Remove $HOME/.3T/studio-3t/
echo "Removing $HOME/.3T/studio-3t/..."
if [ -d "$HOME/.3T/studio-3t/" ]; then
    rm -rf "$HOME/.3T/studio-3t/" 2>/dev/null || true
elif [ -f "$HOME/.3T/studio-3t/" ]; then
    rm -f "$HOME/.3T/studio-3t/" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/3t.enterprise.mongochef.plist
echo "Removing $HOME/Library/Preferences/3t.enterprise.mongochef.plist..."
if [ -d "$HOME/Library/Preferences/3t.enterprise.mongochef.plist" ]; then
    rm -rf "$HOME/Library/Preferences/3t.enterprise.mongochef.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/3t.enterprise.mongochef.plist" ]; then
    rm -f "$HOME/Library/Preferences/3t.enterprise.mongochef.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/3t.mongochef.core.plist
echo "Removing $HOME/Library/Preferences/3t.mongochef.core.plist..."
if [ -d "$HOME/Library/Preferences/3t.mongochef.core.plist" ]; then
    rm -rf "$HOME/Library/Preferences/3t.mongochef.core.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/3t.mongochef.core.plist" ]; then
    rm -f "$HOME/Library/Preferences/3t.mongochef.core.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/3t.mongochef.enterprise.plist
echo "Removing $HOME/Library/Preferences/3t.mongochef.enterprise.plist..."
if [ -d "$HOME/Library/Preferences/3t.mongochef.enterprise.plist" ]; then
    rm -rf "$HOME/Library/Preferences/3t.mongochef.enterprise.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/3t.mongochef.enterprise.plist" ]; then
    rm -f "$HOME/Library/Preferences/3t.mongochef.enterprise.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/3t.mongochef.pro.plist
echo "Removing $HOME/Library/Preferences/3t.mongochef.pro.plist..."
if [ -d "$HOME/Library/Preferences/3t.mongochef.pro.plist" ]; then
    rm -rf "$HOME/Library/Preferences/3t.mongochef.pro.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/3t.mongochef.pro.plist" ]; then
    rm -f "$HOME/Library/Preferences/3t.mongochef.pro.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
