#!/bin/bash
# Uninstall script for Syncovery
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Syncovery..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Syncovery if running..."
pkill -f "Syncovery" 2>/dev/null || true

# Remove $HOME/Library/Application Support/Syncovery
echo "Removing $HOME/Library/Application Support/Syncovery..."
if [ -d "$HOME/Library/Application Support/Syncovery" ]; then
    rm -rf "$HOME/Library/Application Support/Syncovery" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Syncovery" ]; then
    rm -f "$HOME/Library/Application Support/Syncovery" 2>/dev/null || true
fi

# Remove $HOME/Library/Logs/Syncovery
echo "Removing $HOME/Library/Logs/Syncovery..."
if [ -d "$HOME/Library/Logs/Syncovery" ]; then
    rm -rf "$HOME/Library/Logs/Syncovery" 2>/dev/null || true
elif [ -f "$HOME/Library/Logs/Syncovery" ]; then
    rm -f "$HOME/Library/Logs/Syncovery" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/Syncovery
echo "Removing $HOME/Library/Preferences/Syncovery..."
if [ -d "$HOME/Library/Preferences/Syncovery" ]; then
    rm -rf "$HOME/Library/Preferences/Syncovery" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/Syncovery" ]; then
    rm -f "$HOME/Library/Preferences/Syncovery" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/Syncovery.ini
echo "Removing $HOME/Library/Preferences/Syncovery.ini..."
