if [ -d "$HOME/Library/Saved Application State/com.company.Syncovery.savedState" ]; then
    rm -rf "$HOME/Library/Saved Application State/com.company.Syncovery.savedState" 2>/dev/null || true
elif [ -f "$HOME/Library/Saved Application State/com.company.Syncovery.savedState" ]; then
    rm -f "$HOME/Library/Saved Application State/com.company.Syncovery.savedState" 2>/dev/null || true
fi

