#!/bin/bash
# Uninstall script for TeaCode
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling TeaCode..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping TeaCode if running..."
pkill -f "TeaCode" 2>/dev/null || true

# Remove /Applications/TeaCode.app
echo "Removing /Applications/TeaCode.app..."
if [ -d "/Applications/TeaCode.app" ]; then
    rm -rf "/Applications/TeaCode.app" 2>/dev/null || true
elif [ -f "/Applications/TeaCode.app" ]; then
    rm -f "/Applications/TeaCode.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.apptorium.TeaCode-dm.plist
echo "Removing $HOME/Library/Preferences/com.apptorium.TeaCode-dm.plist..."
if [ -d "$HOME/Library/Preferences/com.apptorium.TeaCode-dm.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.apptorium.TeaCode-dm.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.apptorium.TeaCode-dm.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.apptorium.TeaCode-dm.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
