# Remove /Applications/Threema.app
echo "Removing /Applications/Threema.app..."
if [ -d "/Applications/Threema.app" ]; then
    rm -rf "/Applications/Threema.app" 2>/dev/null || true
elif [ -f "/Applications/Threema.app" ]; then
    rm -f "/Applications/Threema.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/threema-consumer-web
