#!/bin/bash
# Uninstall script for Vimcal
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Vimcal..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Vimcal if running..."
pkill -f "Vimcal" 2>/dev/null || true

# Remove /Applications/Vimcal.app
echo "Removing /Applications/Vimcal.app..."
if [ -d "/Applications/Vimcal.app" ]; then
    rm -rf "/Applications/Vimcal.app" 2>/dev/null || true
elif [ -f "/Applications/Vimcal.app" ]; then
    rm -f "/Applications/Vimcal.app" 2>/dev/null || true
fi

# Remove $HOME/Library/Application Support/com.apple.sharedfilelist/com.apple.LSSharedFileList.ApplicationRecentDocuments/com.vimcal.app.sfl*
