fi

# Remove $HOME/Library/Preferences/com.vimcal.app.plist
echo "Removing $HOME/Library/Preferences/com.vimcal.app.plist..."
if [ -d "$HOME/Library/Preferences/com.vimcal.app.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.vimcal.app.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.vimcal.app.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.vimcal.app.plist" 2>/dev/null || true
fi

# Remove $HOME/Library/Saved Application State/com.vimcal.app.savedState
echo "Removing $HOME/Library/Saved Application State/com.vimcal.app.savedState..."
