#!/bin/bash
# Uninstall script for Wire
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Wire..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Wire if running..."
pkill -f "Wire" 2>/dev/null || true

# Remove $HOME/Library/Containers/com.wearezeta.zclient.mac
echo "Removing $HOME/Library/Containers/com.wearezeta.zclient.mac..."
if [ -d "$HOME/Library/Containers/com.wearezeta.zclient.mac" ]; then
    rm -rf "$HOME/Library/Containers/com.wearezeta.zclient.mac" 2>/dev/null || true
elif [ -f "$HOME/Library/Containers/com.wearezeta.zclient.mac" ]; then
    rm -f "$HOME/Library/Containers/com.wearezeta.zclient.mac" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
