#!/bin/bash
# Uninstall script for Witch
# Generated by IntuneBrew

# Exit on error
set -e

echo "Uninstalling Witch..."

# Check if running as root
if [ "$EUID" -ne 0 ]; then
  echo "Please run as root"
  exit 1
fi

# Kill application process if running
echo "Stopping Witch if running..."
pkill -f "Witch" 2>/dev/null || true

# Kill application with bundle ID com.manytricks.witchdaemon if running
echo "Stopping application with bundle ID com.manytricks.witchdaemon if running..."
killall -9 "com.manytricks.witchdaemon" 2>/dev/null || true

# Remove $HOME/Library/Application Support/Witch
echo "Removing $HOME/Library/Application Support/Witch..."
if [ -d "$HOME/Library/Application Support/Witch" ]; then
    rm -rf "$HOME/Library/Application Support/Witch" 2>/dev/null || true
elif [ -f "$HOME/Library/Application Support/Witch" ]; then
    rm -f "$HOME/Library/Application Support/Witch" 2>/dev/null || true
fi

# Remove $HOME/Library/Preferences/com.manytricks.Witch.plist
echo "Removing $HOME/Library/Preferences/com.manytricks.Witch.plist..."
if [ -d "$HOME/Library/Preferences/com.manytricks.Witch.plist" ]; then
    rm -rf "$HOME/Library/Preferences/com.manytricks.Witch.plist" 2>/dev/null || true
elif [ -f "$HOME/Library/Preferences/com.manytricks.Witch.plist" ]; then
    rm -f "$HOME/Library/Preferences/com.manytricks.Witch.plist" 2>/dev/null || true
fi

echo "Uninstallation complete!"
exit 0
